import React from 'react';
import { Query } from "react-apollo";
import { gql } from '@apollo/client';
const Courses = () => (
  <Query
    query={gql`
      query GetRates {
        rates(currency: "USD") {
          currency
        }
      }
    `}
  >
    {({ loading, error, data }) => {
      if (loading) return <p>Loading...</p>;
      if (error) return <p>Error :(</p>;
      return data.responseHeader.response.docs.map(({ id, currency }) => (
        <div key={id}>
          <p>{`${currency} by ${currency}`}</p>
        </div>
      ));
    }}
  </Query>
);
export default Courses;
