import React, { Component} from 'react';
import { Query } from "react-apollo";
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import InsertDriveFileIcon from '@material-ui/icons/InsertDriveFile';
import Divider from '@material-ui/core/Divider';
import Paper from "@material-ui/core/Paper";
import Style from './Style'
import { gql } from '@apollo/client';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/styles';

export  const styles = theme => ({
  heading:{
    alignItems: "center",
    fontSize: 33,
    fontWeight: 300,
    letterSpacing: "0.0075em",
    verticalAlign: "middle",
    alignItems: "center",
    textAlign: "center",
    color:'#2196F3'
  },
  root: {
    padding: "2px 4px",
    display: "flex",
    alignItems: "center",
    width: 700,
    margin: `${theme.spacing()}px auto`
  },
  input: {
    flex: 2,
    alignItems: "center",
    paddingLeft: `calc(1em + ${theme.spacing(1)}px)`,
    borderTopLeftRadius: '4px',
    borderTopRightRadius: '4px'
  },
  iconButton: {
    //padding: 5,
    background: 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
    border: 1,

    boxShadow: '1 3px 5px 2px rgba(255, 105, 135, .3)',
    color: 'white',
    height:40,
    padding: '0px 10px'
  },
  divider: {
    margin: `${theme.spacing()}px auto`
  },
  paper: {
    maxWidth: 700,
    margin: `${theme.spacing()}px auto`,
    padding: theme.spacing(2),
  },
  pagination: {
    maxWidth: 700,
    margin: `${theme.spacing()}px auto`,
    padding: theme.spacing(2),
    alignItems: "center",
    verticalAlign: "middle",
    alignItems: "center",
    textAlign: "center"
  },
  section1: {
    margin: theme.spacing(1, 2),
  },
  section2: {
    margin: theme.spacing(2),

  },
  section3: {
    margin: theme.spacing(2),
    //background: 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
    border: 1,

    boxShadow: '1 3px 5px 2px rgba(255, 105, 135, .3)'

  },
  buttons: {
    '& > *': {
      margin: theme.spacing(1),
    },
  }
});

export const getSolrData = gql`
      query Data($query: String){
        responseHeader(row: "5", start:"1",query: $query){
          response{
            numFound
            docs{
              id
              data
              resource
            }
          }
        }
      }
      `;

class Solr extends Component{
    render(){
      const { classes } = this.props
        return <Query query={getSolrData} variables={{ query: this.props.value }}>
        {({ loading, error, data }) => {
          if (loading) return <p>Loading...</p>;
          if (error) return <p>Error :(</p>;
          return data.responseHeader.response.docs.map(({ id, data, resource }) => (
            <div key={id}>
                <Paper className={classes.paper}>
                <div className={classes.section1}>
                <Grid container wrap="nowrap" spacing={2}>
                  <Grid item xs={12}>
                    <Typography noWrap color="textSecondary" variant="body2"> 
                    <a href={resource} target="_blank"> <InsertDriveFileIcon /></a>
                    </Typography>
                  </Grid>
                </Grid>
                </div>
                <Divider variant="middle" className={classes.Divider} />
                <div className={classes.section2}>
                <Grid container wrap="nowrap" spacing={2}>
                  <Grid item xs={12}>
                    <Typography noWrap>{data}</Typography>
                  </Grid>
                </Grid>
                </div>
              </Paper>
            </div>
          ));
        }}
      </Query>
    }
}

Solr.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Solr);
