import { gql } from 'apollo-boost';


const getSolrData = gql`
      query Data($query: String, $start: String, $row: String){
        responseHeader(row: $row, start: $start,query: $query){
          response{
            numFound
            docs{
              id
              data
              resource
            }
          }
        }
      }
      `;

export default getSolrData ;
