import { gql } from 'apollo-boost';


const getSolrData = gql`
      query Data($query: String, $row: String){
        responseHeader(query: $query, row:$row, start:"1"){
          response{
            numFound
            docs{
              id
              data
              resource
            }
          }
        }
      }
      `;

export default getSolrData ;
