import React, { Component} from 'react';
import { Query } from "react-apollo";
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import InsertDriveFileIcon from '@material-ui/icons/InsertDriveFile';


class Solr extends Component{
    render(){
        return <Query query={this.props.getSolrData} variables={{ query: this.props.value }}>
        {({ loading, error, data }) => {
          if (loading) return <p>Loading...</p>;
          if (error) return <p>Error :(</p>;
          return data.responseHeader.response.docs.map(({ id, data, resource }) => (
            <div key={id}>
                <Paper className={classes.paper}>
                <div className={classes.section1}>
                <Grid container wrap="nowrap" spacing={2}>
                  <Grid item xs={12}>
                    <Typography noWrap color="textSecondary" variant="body2"> 
                    <a href={resource} target="_blank"> <InsertDriveFileIcon /></a>
                    </Typography>
                  </Grid>
                </Grid>
                </div>
                <Divider variant="middle" className={classes.Divider} />
                <div className={classes.section2}>
                <Grid container wrap="nowrap" spacing={2}>
                  <Grid item xs={12}>
                    <Typography noWrap>{data}</Typography>
                  </Grid>
                </Grid>
                </div>
              </Paper>
            </div>
          ));
        }}
      </Query>
    }
}

export default Solr
