import React from 'react';
import './App.css';
import ApolloClient from "apollo-boost";
import { ApolloProvider } from "react-apollo";
import Divider from '@material-ui/core/Divider';
import Paper from "@material-ui/core/Paper";
import InputBase from "@material-ui/core/InputBase";
import SearchIcon from "@material-ui/icons/Search";
import { makeStyles } from "@material-ui/core/styles";
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import Pagination from "@material-ui/lab/Pagination";
import Solr from './Solr'
import Slide from '@material-ui/core/Slide';




const useStyles = makeStyles((theme) => ({
  heading:{
    alignItems: "center",
    fontSize: 33,
    fontWeight: 300,
    letterSpacing: "0.0075em",
    verticalAlign: "middle",
    alignItems: "center",
    textAlign: "center",
    color:'#2196F3'
  },
  root: {
    padding: "2px 4px",
    display: "flex",
    alignItems: "center",
    width: 700,
    margin: `${theme.spacing()}px auto`
  },
  input: {
    flex: 2,
    alignItems: "center",
    paddingLeft: `calc(1em + ${theme.spacing(1)}px)`,
    borderTopLeftRadius: '4px',
    borderTopRightRadius: '4px'
  },
  iconButton: {
    //padding: 5,
    background: 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
    border: 1,

    boxShadow: '1 3px 5px 2px rgba(255, 105, 135, .3)',
    color: 'white',
    height:40,
    padding: '0px 10px'
  },
  divider: {
    margin: `${theme.spacing()}px auto`
  },
  paper: {
    maxWidth: 700,
    margin: `${theme.spacing()}px auto`,
    padding: theme.spacing(2),
  },
  pagination: {
    maxWidth: 700,
    margin: `${theme.spacing()}px auto`,
    padding: theme.spacing(2),
    alignItems: "center",
    verticalAlign: "middle",
    alignItems: "center",
    textAlign: "center"
  },
  section1: {
    margin: theme.spacing(1, 2),
  },
  section2: {
    margin: theme.spacing(2),

  },
  section3: {
    margin: theme.spacing(2),
    //background: 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
    border: 1,

    boxShadow: '1 3px 5px 2px rgba(255, 105, 135, .3)'

  },
  buttons: {
    '& > *': {
      margin: theme.spacing(1),
    },
  }
}));

const client = new ApolloClient({
  uri: "http://localhost:4000/"
});


export default function App() {
  const [value, setValue] = React.useState()
  const [start, setStart] = React.useState(0);
  const [row, setRow] = React.useState(5);
  const [page, setPage] = React.useState(1);
  const [checked, setChecked] = React.useState(false);

  const handleChange = () => {

  };


  const onSubmit =  e => {
    e.PreventDefault();
  };


  const classes = useStyles();

  return (

      <div>
        <div className={classes.heading}>
        <Typography variant="h5"><b>KSearchE</b></Typography>
        </div>
        <ApolloProvider client={client}>

        <Paper component="form" className={classes.root}>
          <InputBase
            classes={{root:classes.input}}
            placeholder="Search"
            inputProps={{ "aria-label": "search google maps" }}
            onChange={event=>{ //adding the onChange event
              setValue(event.target.value)
            }}

          />
          <IconButton type="submit"  color="primary" className={classes.iconButton} aria-label="search" onClick= {onSubmit} > 
            <SearchIcon/>
          </IconButton>
      </Paper>
      <Divider className={classes.section3} />   
      <Solr query={value} row={row.toString()} start={start.toString()} client={client}/>
      <Pagination
              className={classes.paper}
              checked={checked}
              count={10}
              page={Number(page)}
              siblingCount={1}
              boundaryCount={1}
              variant="outlined"
              shape="rounded"
              onChange={(event, value) =>{ //adding the onChange event
                setChecked((prev) => !prev);
                setPage(value)

                if (value == 1) {
                  setStart(0)
                } else {
                  setStart((value*5)-5)
                }
                setRow(5)

              }}
            />
      </ApolloProvider>
    </div>




)};
