import React from 'react';
import './App.css';
import ApolloClient from "apollo-boost";
import { ApolloProvider } from "react-apollo";
import Paper from "@material-ui/core/Paper";
import InputBase from "@material-ui/core/InputBase";
import IconButton from "@material-ui/core/IconButton";
import SearchIcon from "@material-ui/icons/Search";
import { makeStyles } from "@material-ui/core/styles";
import Courses from './Courses'


const useStyles = makeStyles((theme) => ({
  root: {
    padding: "2px 4px",
    display: "flex",
    alignItems: "center",
    width: 800
  },
  input: {
    marginLeft: theme.spacing(1),
    flex: 1
  },
  iconButton: {
    padding: 10
  },
  divider: {
    height: 28,
    margin: 4
  }
}));



const client = new ApolloClient({
  uri: "http://localhost:4000/"
});

export default function App() {
  const [value, setValue] = React.useState()

  const onSubmit = e => {
    e.preventDefault();

  };

  const executeSearch = async () => {
    <div>
       <Query
    query={gql`
      query{
        responseHeader(row:"7",query:"jokowi"){
          response{
            numFound
            docs{
              source
              id
            }
          }
        }
      }
    `}
  >
    {({ loading, error, data }) => {
      if (loading) return <p>Loading...</p>;
      if (error) return <p>Error :(</p>;
      return data.responseHeader.response.docs.map(({ id, source, data }) => (
        <div key={id}>
          <p>{`${source} by ${data}`}</p>
        </div>
      ));
    }}
  </Query>
    </div>

  }

  const classes = useStyles();

  return (
    <ApolloProvider client={client}>

      <Paper component="form" className={classes.root}>
        <InputBase
          className={classes.input}
          placeholder="Solr Search"
          inputProps={{ "aria-label": "search google maps" }}
          onChange={event=>{                                 //adding the onChange event
            setValue(event.target.value)
          }}

        />
        <IconButton type="submit" className={classes.iconButton} aria-label="search" onClick= {onSubmit} >
          <SearchIcon />
        </IconButton>
    </Paper>

  </ApolloProvider>

)};
