import React from 'react';
import './App.css';
import ApolloClient from "apollo-boost";
import { ApolloProvider } from "react-apollo";
import { ApolloConsumer } from 'react-apollo';
import Paper from "@material-ui/core/Paper";
import InputBase from "@material-ui/core/InputBase";
import IconButton from "@material-ui/core/IconButton";
import SearchIcon from "@material-ui/icons/Search";
import { makeStyles } from "@material-ui/core/styles";
import { gql } from '@apollo/client';
import { Query } from "react-apollo";

//import Courses from './Courses'



const useStyles = makeStyles((theme) => ({
  root: {
    padding: "2px 4px",
    display: "flex",
    alignItems: "center",
    width: 500
  },
  input: {
    marginLeft: theme.spacing(1),
    flex: 2,
    alignItems: "center"

  },
  iconButton: {
    padding: 10
  },
  divider: {
    height: 28,
    margin: 4
  }
}));

const client = new ApolloClient({
  uri: "http://localhost:4000/"
});

export const getSolrData = gql`
      query{
        responseHeader(row: "7", query: $query){
          response{
            numFound
            docs{
              source
              id
              data
            }
          }
        }
      }
      `;

export default function App() {
  const [value, setValue] = React.useState()

  const onSubmit =  e => {
    e.PreventDefault();
    setValue(e.target.value)    
  };

  const classes = useStyles();

  return (
      <div>
      <ApolloProvider client={client}>
      <Paper component="form" className={classes.root}>
        <InputBase
          className={classes.input}
          placeholder="Solr Search"
          inputProps={{ "aria-label": "search google maps" }}
          onChange={event=>{ //adding the onChange event
            setValue(event.target.value)
          }}

        />
        <IconButton type="submit" className={classes.iconButton} aria-label="search" onClick= {onSubmit} >
          <SearchIcon />
        </IconButton>
    </Paper>
    <Query query={getSolrData} variables={value}>
    {({ loading, error, data }) => {
      if (loading) return <p>Loading...</p>;
      if (error) return <p>Error :(</p>;
      return data.responseHeader.response.docs.map(({ id, source, data }) => (
        <div key={id}>
            <Paper className={classes.paper}>
            <Grid container wrap="nowrap" spacing={2}>
              <Grid item xs={12}>
                <Typography noWrap>{data}</Typography>
              </Grid>
            </Grid>
            <Grid container wrap="nowrap" spacing={2}>
              <Grid item xs={12}>
                <Typography noWrap><a href={source}>{source}</a></Typography>
              </Grid>
            </Grid>
          </Paper>
        </div>
      ));
    }}

    </Query>
    </ApolloProvider>
    </div>




)};
