import React from 'react';
import { Query } from "react-apollo";
import { gql } from '@apollo/client';


class Courses extends React.Component {
  render(){
  return (
  <div> 
  <Query
    query={gql`
      query{
        responseHeader(row:"7",query:"jokowi"){
          response{
            numFound
            docs{
              source
              id
            }
          }
        }
      }
    `}
  >
    {({ loading, error, data }) => {
      if (loading) return <p>Loading...</p>;
      if (error) return <p>Error :(</p>;
      return data.responseHeader.response.docs.map(({ id, source, data }) => (
        <div key={id}>
          <p>{`${source} by ${data}`}</p>
        </div>
      ));
    }}
  </Query>
  </div>
)
}
}
