import React, {Component} from 'react';
import { Query } from "react-apollo";
import { graphql } from 'react-apollo';
import { gql } from '@apollo/client';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import { getSolrData } from '../queries/queries';


const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    overflow: 'hidden',
    padding: theme.spacing(0, 3),
  },
  paper: {
    maxWidth: 400,
    margin: `${theme.spacing()}px auto`,
    padding: theme.spacing(2),
  },
}));




class Courses extends Component  {

  displayBookDetails(){
    const { data } = this.props.data;
    if(data){
        return(
          <div>
          { data.responseHeader.response.docs.map( (id,source,data) => {
            <div key={id}>
                <Paper className={classes.paper}>
                <Grid container wrap="nowrap" spacing={2}>
                  <Grid item xs={12}>
                    <Typography noWrap>{data}</Typography>
                  </Grid>
                </Grid>
                <Grid container wrap="nowrap" spacing={2}>
                  <Grid item xs={12}>
                    <Typography noWrap><a href={source}>{source}</a></Typography>
                  </Grid>
                </Grid>
              </Paper>
            </div>
          })}
          </div>

        );
    } else {
        return( <div>None...</div> );
    }
}

  render(){

    return (
        <div> 
          <div id="book-details">
                { this.displayBookDetails() }
            </div>
        </div>
    )
  }
}

//export default () => {
  //const classes = useStyles();
  //return (
    //  <Courses classes={classes} />
  //)
//}

export default graphql(getSolrData, {
  options: (props) => {
    const classes = useStyles();
      return {
          variables: {
              query: props.query
          }
      }
  }
})(Courses);
