import React from 'react';
import './App.css';
import ApolloClient from "apollo-boost";
import { ApolloProvider } from "react-apollo";
import Divider from '@material-ui/core/Divider';
import Paper from "@material-ui/core/Paper";
import InputBase from "@material-ui/core/InputBase";
import Button from "@material-ui/core/IconButton";
import SearchIcon from "@material-ui/icons/Search";
import { makeStyles } from "@material-ui/core/styles";
import { gql } from '@apollo/client';
import { Query } from "react-apollo";
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import PageviewIcon from '@material-ui/icons/Pageview';
import IconButton from '@material-ui/core/IconButton';


const useStyles = makeStyles((theme) => ({
  inputInput: {
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('md')]: {
      width: '20ch',
    },
  },
  heading:{
    alignItems: "center",
    fontSize: 33,
    fontWeight: 300,
    letterSpacing: "0.0075em",
    verticalAlign: "middle",
    alignItems: "center",
    textAlign: "center"
  },
  root: {
    padding: "2px 4px",
    display: "flex",
    alignItems: "center",
    width: 700,
    margin: `${theme.spacing()}px auto`
  },
  input: {
    marginLeft: theme.spacing(1),
    flex: 2,
    alignItems: "center",
    paddingLeft: `calc(1em + ${theme.spacing(1)}px)`
  },
  iconButton: {
    padding: 5,
    background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
    border: 0,
    borderRadius: 2,
    boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
    color: 'white',
    height: 48,
    padding: '0 30px'
  },
  divider: {
    margin: `${theme.spacing()}px auto`
  },
  paper: {
    maxWidth: 700,
    margin: `${theme.spacing()}px auto`,
    padding: theme.spacing(2),
  },
  section1: {
    margin: theme.spacing(1, 2),
  },
  section2: {
    margin: theme.spacing(2),
  },
  buttons: {
    '& > *': {
      margin: theme.spacing(1),
    },
  }
}));

const client = new ApolloClient({
  uri: "http://localhost:4000/"
});

export const getSolrData = gql`
      query Data($query: String){
        responseHeader(row: "7", query: $query){
          response{
            numFound
            docs{
              articleid
              content
              title
              datee
            }
          }
        }
      }
      `;

export default function App() {
  const [value, setValue] = React.useState()

  const onSubmit =  e => {
    e.PreventDefault();
  };

  const classes = useStyles();

  return (

      <div>
        <div className={classes.heading}>
        <Typography variant="h5">KSearchE</Typography>
        </div>
        <ApolloProvider client={client}>
        <Paper component="form" className={classes.root}>
          <InputBase
            classes={{root:classes.input}}
            placeholder="Search"
            inputProps={{ "aria-label": "search google maps" }}
            onChange={event=>{ //adding the onChange event
              setValue(event.target.value)
            }}

          />
          <IconButton type="submit"  color="primary" className={classes.iconButton} aria-label="search" onClick= {onSubmit} > 
            <SearchIcon/>
          </IconButton>
      </Paper>
      <Query query={getSolrData} variables={{ query: value }}>
        {({ loading, error, data }) => {
          if (loading) return <p>Loading...</p>;
          if (error) return <p>Error :(</p>;
          return data.responseHeader.response.docs.map(({ articleid, content, title }) => (
            <div key={articleid}>
                <Paper className={classes.paper}>
                <div className={classes.section1}>
                <Grid container wrap="nowrap" spacing={2}>
                  <Grid item xs={12}>
                    <Typography color="textSecondary" variant="body2">
                      <b>{title}</b>
                    </Typography>
                  </Grid>
                </Grid>
                </div>
                <Divider variant="middle" className={classes.Divider} />
                <div className={classes.section2}>
                <Grid container wrap="nowrap" spacing={2}>
                  <Grid item xs={12}>
                    <Typography noWrap>{content}</Typography>
                  </Grid>
                </Grid>
                </div>
              </Paper>
            </div>
          ));
        }}
      </Query>
      </ApolloProvider>
    </div>




)};
