import React, {Component} from 'react';
import { Query } from "react-apollo";
import { gql } from '@apollo/client';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import { getSolrData } from '../queries/queries';


const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    overflow: 'hidden',
    padding: theme.spacing(0, 3),
  },
  paper: {
    maxWidth: 400,
    margin: `${theme.spacing()}px auto`,
    padding: theme.spacing(2),
  },
}));




class Courses extends Component  {


  render(){
    const classes = this.props.classes;
  return (
  <div> 
  <Query
    query={gql`
      query{
        responseHeader(row:"7",query:${this.props.value}){
          response{
            numFound
            docs{
              source
              id
              data
            }
          }
        }
      }
    `}
  >
    {({ loading, error, data }) => {
      if (loading) return <p>Loading...</p>;
      if (error) return <p>Error :(</p>;
      return data.responseHeader.response.docs.map(({ id, source, data }) => (
        <div key={id}>
            <Paper className={classes.paper}>
            <Grid container wrap="nowrap" spacing={2}>
              <Grid item xs={12}>
                <Typography noWrap>{data}</Typography>
              </Grid>
            </Grid>
            <Grid container wrap="nowrap" spacing={2}>
              <Grid item xs={12}>
                <Typography noWrap><a href={source}>{source}</a></Typography>
              </Grid>
            </Grid>
          </Paper>
        </div>

      ));
    }}
  </Query>
  </div>
)
  }
}

//export default () => {
  //const classes = useStyles();
  //return (
    //  <Courses classes={classes} />
  //)
//}

export default graphql(getSolrData, {
  options: (props) => {
      return {
          variables: {
              id: props.bookId
          }
      }
  }
})(Courses);
