import React from 'react';
import './App.css';
import ApolloClient from "apollo-boost";
import { ApolloProvider } from "react-apollo";
import Divider from '@material-ui/core/Divider';
import Paper from "@material-ui/core/Paper";
import InputBase from "@material-ui/core/InputBase";
import IconButton from "@material-ui/core/IconButton";
import SearchIcon from "@material-ui/icons/Search";
import { makeStyles } from "@material-ui/core/styles";
import { gql } from '@apollo/client';
import { Query } from "react-apollo";
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';



//import Courses from './Courses'



const useStyles = makeStyles((theme) => ({
  root: {
    padding: "2px 4px",
    display: "flex",
    alignItems: "center",
    width: 400,
    margin: `${theme.spacing()}px auto`
  },
  input: {
    marginLeft: theme.spacing(1),
    flex: 2,
    alignItems: "center"

  },
  iconButton: {
    padding: 10
  },
  divider: {
    margin: `${theme.spacing()}px auto`
  },
  paper: {
    maxWidth: 700,
    margin: `${theme.spacing()}px auto`,
    padding: theme.spacing(2),
    padding: "23px 23px"
  },
  section1: {
    margin: theme.spacing(3, 2),
  },
  section2: {
    margin: theme.spacing(2),
  }
}));

const client = new ApolloClient({
  uri: "http://localhost:4000/"
});

export const getSolrData = gql`
      query Data($query: String){
        responseHeader(row: "7", query: $query){
          response{
            numFound
            docs{
              articleid
              content
              title
              datee
            }
          }
        }
      }
      `;

export default function App() {
  const [value, setValue] = React.useState()

  const onSubmit =  e => {
    e.PreventDefault();
    alert(value)  
  };

  const classes = useStyles();

  return (
      <div>
      <ApolloProvider client={client}>
      <Paper component="form" className={classes.root}>
        <InputBase
          className={classes.input}
          placeholder="Solr Search"
          inputProps={{ "aria-label": "search google maps" }}
          onChange={event=>{ //adding the onChange event
            setValue(event.target.value)
          }}

        />
        <IconButton type="submit" className={classes.iconButton} aria-label="search" onClick= {onSubmit} color="primary" >
          <SearchIcon />
        </IconButton>
    </Paper>
    <Query query={getSolrData} variables={{ query: value }}>
    {({ loading, error, data }) => {
      if (loading) return <p>Loading...</p>;
      if (error) return <p>Error :(</p>;
      return data.responseHeader.response.docs.map(({ articleid, content, title }) => (
        <div key={articleid}>
            <Paper className={classes.paper}>
            <div className={classes.section1}>
            <Grid container wrap="nowrap" spacing={2}>
              <Grid item xs={12}>
                <Typography color="textSecondary" variant="body2"><b>{title}</b></Typography>
              </Grid>
            </Grid>
            </div>
            <Divider variant="middle" className={classes.Divider} />
            <div className={classes.section}>
            <Grid container wrap="nowrap" spacing={2}>
              <Grid item xs={12}>
                <Typography noWrap>{content}</Typography>
              </Grid>
            </Grid>
            </div>
          </Paper>
        </div>
      ));
    }}

    </Query>
    </ApolloProvider>
    </div>




)};
